from rest_framework import permissionsclass IsStaffReadOnly(permissions.BasePermission):    def has_permission(self, request, view):        if request.method in permissions.SAFE_METHODS:            return bool(request.user and request.user.is_staff)class IsAuthenticatedWriteOnly(permissions.BasePermission):    def has_permission(self, request, view):        if request.method == "POST":            return bool(not request.user.is_staff)